// import React, { Component } from 'react';
// import { Route } from 'react-router-dom';

// import HousePlants from './HousePlants';

// import './custom.css'

// export default class App extends Component {
//   static displayName = App.name;

//   render () {
//     return (
  
//         <Route exact path='/' component={HousePlants} />
   
//     );
//   }
// }
import React, { useState, useEffect } from 'react';
import axios from 'axios';
 import { Table, Dropdown, FormControl } from 'react-bootstrap';
import { Link, Route, Switch, useHistory, useLocation } from 'react-router-dom';

const App = () => {
  const [plants, setPlants] = useState([]);
  const [filteredPlants, setFilteredPlants] = useState([]);
  const [categoryFilter, setCategoryFilter] = useState('');
  const [searchTerm, setSearchTerm] = useState('');

  // Fetch data from API when component mounts
  useEffect(() => {
    const fetchData = async () => {
      const result = await axios(
        'https://house-plants2.p.rapidapi.com/plants',
        {
          method: 'GET',
          headers: {
            'X-RapidAPI-Host': 'house-plants2.p.rapidapi.com',
            'X-RapidAPI-Key': 'YOUR_API_KEY',
          },
        }
      );
      setPlants(result.data);
      setFilteredPlants(result.data);
    };
    fetchData();
  }, []);

  // Filter plants by category
  useEffect(() => {
    if (categoryFilter !== '') {
      const filteredData = plants.filter(
        (plant) => plant.category.toLowerCase() === categoryFilter.toLowerCase()
      );
      setFilteredPlants(filteredData);
    } else {
      setFilteredPlants(plants);
    }
  }, [categoryFilter, plants]);

  // Filter plants by search term
  useEffect(() => {
    if (searchTerm !== '') {
      const filteredData = plants.filter(
        (plant) =>
          plant.latinName.toLowerCase().includes(searchTerm) ||
          plant.family.toLowerCase().includes(searchTerm)
      );
      setFilteredPlants(filteredData);
    } else {
      setFilteredPlants(plants);
    }
  }, [searchTerm, plants]);

  const handleCategoryChange = (event) => {
    setCategoryFilter(event.target.value);
  };

  const handleSearchTermChange = (event) => {
    setSearchTerm(event.target.value);
  };

  return (
    <>
      <h1>House Plants</h1>
      <div>
        <Dropdown>
          <Dropdown.Toggle variant="success" id="dropdown-basic">
            {categoryFilter === '' ? 'All Categories' : categoryFilter}
          </Dropdown.Toggle>

          <Dropdown.Menu>
            <Dropdown.Item onClick={() => setCategoryFilter('')}>
              All Categories
            </Dropdown.Item>
            <Dropdown.Item onClick={() => setCategoryFilter('Indoor')}>
              Indoor
            </Dropdown.Item>
            <Dropdown.Item onClick={() => setCategoryFilter('Outdoor')}>
              Outdoor
            </Dropdown.Item>
            <Dropdown.Item onClick={() => setCategoryFilter('Fruit Trees')}>
              Fruit Trees
            </Dropdown.Item>
          </Dropdown.Menu>
        </Dropdown>

        <FormControl
          type="text"
          placeholder="Search by Latin Name or Family"
          className="mr-sm-2"
          onChange={handleSearchTermChange}
          value={searchTerm}
        />
      </div>

      <Table striped bordered hover>
        <thead>
          <tr>
            <th>Latin Name</th>
            <th>Family</th>
            <th>Category</th>
            <th>Style</th>
            <th>Growth</th>
          </tr>
        </thead>
        <tbody>
          {filteredPlants.map((plant) => (
            <tr key={plant.id}>
              <td>
                <Link to={`/plant/${plant.id}`}>{plant.latinName}</Link>
              </td>
              <td>{plant.family}</td>
              <td>{plant.category}</td>
              <td>{plant.style}</td>
              <td>{plant.growth}</td>
            </tr>
          ))}
        </tbody>
      </Table>

      <Switch>
        <Route path="/plant/:id">
          <PlantDetails />
        </Route>
      </Switch>
    </>
  );
};

const PlantDetails = () => {
  const [plant, setPlant] = useState(null);
  const history = useHistory();
  const location = useLocation();

  useEffect(() => {
    const fetchData = async () => {
      const result = await axios(
        `https://house-plants2.p.rapidapi.com/plants/${location.pathname.split('/')[2]}`,
        {
          method: 'GET',
          headers: {
            'X-RapidAPI-Host': 'house-plants2.p.rapidapi.com',
            'X-RapidAPI-Key': 'YOUR_API_KEY',
          },
        }
      );
      setPlant(result.data);
    };
    fetchData();
  }, [location]);

  const handleBackClick = () => {
    history.goBack();
  };

  if (!plant) {
    return null;
  }

  return (
    <>
      <h2>{plant.latinName}</h2>
      <img src={plant.image ? plant.image : 'placeholder.jpg'} />

      <div>
        <b>Family:</b> {plant.family}
      </div>
      <div>
        <b>Category:</b> {plant.category}
      </div>
      <div>
        <b>Style:</b> {plant.style}
      </div>
      <div>
        <b>Growth:</b> {plant.growth}
      </div>

      <button onClick={handleBackClick}>Back</button>
    </>
  );
};

export default App;
