import React from "react";
import { useState, useEffect } from "react";
import { useHistory, useLocation } from "react-router-dom/cjs/react-router-dom.min";
import axios from "axios"



const PlantInfo = () => {
    const [plant, setPlant] = useState(null);
    const history = useHistory();
    const location = useLocation();
  
    useEffect(() => {
      const fetchData = async () => {
        const result = await axios(
          `https://house-plants2.p.rapidapi.com/plants/${location.pathname.split('/')[2]}`,
          {
            method: 'GET',
            headers: {
              'X-RapidAPI-Host': 'house-plants2.p.rapidapi.com',
              'X-RapidAPI-Key': 'YOUR_API_KEY',
            },
          }
        );
        setPlant(result.data);
      };
      fetchData();
    }, [location]);
  
    const handleBackClick = () => {
      history.goBack();
    };
  
    if (!plant) {
      return null;
    }
  
    return (
      <>
        <h2>{plant.latinName}</h2>
        <img src={plant.image ? plant.image : 'placeholder.jpg'} />
  
        <div>
          <b>Family:</b> {plant.family}
        </div>
        <div>
          <b>Category:</b> {plant.category}
        </div>
        <div>
          <b>Style:</b> {plant.style}
        </div>
        <div>
          <b>Growth:</b> {plant.growth}
        </div>
  
        <button onClick={handleBackClick}>Back</button>
      </>
    );
  };
  

  export default PlantInfo
