import React, { useState, useEffect } from "react";
import PlantInfo from "./components/PlantInfo";
import PlantGrid from "./components/PlantGrid";
import { Dropdown, FormControl,  } from "bootstrap";
import axios from "axios";

const HousePlants = () => {
    const [plants, setPlants] = useState([]);
    const [filterCategory, setFilterCategory] = useState('');
    const [searchTerm, setSearchTerm] = useState("");
  

  // Fetch data from API when component mounts
  useEffect(() => {
    const fetchData = async () => {
      const result = await axios(
        'https://house-plants2.p.rapidapi.com/plants',
        {
          method: 'GET',
          headers: {
            'X-RapidAPI-Host': 'house-plants2.p.rapidapi.com',
            'X-RapidAPI-Key': '2ca4704713msh3b05be2231f1465p1e5b4ejsn1fde519286e8',
          },
        }
      );
      setPlants(result.data);
    };
    fetchData();
  }, []);
  

  const handleFilterChange = (event) => {
    setFilterCategory(event.target.value);
    setSearchTerm("");
  };

  const handleSearchChange = (event) => {
    setSearchTerm(event.target.value);
    setFilterCategory('');
  };


  return (
    <div>
      <h1>House Plants</h1>
      <div>
       
        <div class="dropdown">
  <button class="btn btn-secondary dropdown-toggle" type="button" id="dropdownMenu2" data-toggle="dropdown" aria-haspopup="true" aria-expanded="false">
  {filterCategory === '' ? 'All Categories' : filterCategory}
  </button>
  <div class="dropdown-menu" aria-labelledby="dropdownMenu2">
    <button class="dropdown-item" type="button"onClick={() => handleFilterChange('')} >  All Categories</button>
    <button class="dropdown-item" type="button"onClick={() => handleFilterChange('Indoor')} >  Indoor</button>
    <button class="dropdown-item" type="button"onClick={() => handleFilterChange('Outdoor')} >  Outdoor </button>
    <button class="dropdown-item" type="button"onClick={() => handleFilterChange('Fruit Trees')} > Fruit Trees</button>
 
 
  </div>
</div>
        {/* <FormControl
          type="text"
          placeholder="Search by Latin Name or Family"
          className="mr-sm-2"
          onChange={handleSearchChange}
          value={searchTerm}
        /> */}
                  <input class="form-control mr-sm-2" type="search" placeholder="Search by Latin Name or Family" aria-label="Search"onChange={handleSearchChange}
          value={searchTerm}/>
      </div>
      <PlantGrid plants={plants} filterCategory={filterCategory} searchTerm={searchTerm} />
      <PlantInfo />
    </div>
  );
};

export default HousePlants;
