import React, { useState, useEffect } from "react";
import PlantInfo from "./components/PlantInfo";
import PlantGrid from "./components/PlantGrid";
import { Dropdown } from "bootstrap";
import axios from "axios";

const HousePlants = () => {
    const [plants, setPlants] = useState([]);
    const [filterCategory, setFilterCategory] = useState('');
    const [searchTerm, setSearchTerm] = useState("");
  

  // Fetch data from API when component mounts
  useEffect(() => {
    const fetchData = async () => {
      const result = await axios(
        'https://house-plants2.p.rapidapi.com/plants',
        {
          method: 'GET',
          headers: {
            'X-RapidAPI-Host': 'house-plants2.p.rapidapi.com',
            'X-RapidAPI-Key': 'YOUR_API_KEY',
          },
        }
      );
      setPlants(result.data);
    };
    fetchData();
  }, []);
  

  const handleFilterChange = (event) => {
    setFilterCategory(event.target.value);
    setSearchTerm("");
  };

  const handleSearchChange = (event) => {
    setSearchTerm(event.target.value);
    setFilterCategory('');
  };


  return (
    <div>
      <h1>House Plants</h1>
      <div>
        <Dropdown>
          <Dropdown.Toggle variant="success" id="dropdown-basic">
            {categoryFilter === '' ? 'All Categories' : categoryFilter}
          </Dropdown.Toggle>

          <Dropdown.Menu>
            <Dropdown.Item onClick={() => handleFilterChange('')}>
              All Categories
            </Dropdown.Item>
            <Dropdown.Item onClick={() => handleFilterChange('Indoor')}>
              Indoor
            </Dropdown.Item>
            <Dropdown.Item onClick={() => handleFilterChange('Outdoor')}>
              Outdoor
            </Dropdown.Item>
            <Dropdown.Item onClick={() => handleFilterChange('Fruit Trees')}>
              Fruit Trees
            </Dropdown.Item>
          </Dropdown.Menu>
        </Dropdown>

        <FormControl
          type="text"
          placeholder="Search by Latin Name or Family"
          className="mr-sm-2"
          onChange={handleSearchChange}
          value={searchTerm}
        />
      </div>
      <PlantGrid plants={plants} filterCategory={filterCategory} searchTerm={searchTerm} />
      <PlantInfo />
    </div>
  );
};

export default HousePlants;
